function escapeMarkdownV2(text) {
  const specialChars = '_*[]()~`>#+-=|{}.!\\';
  let result = '';

  for (const char of text) {
    if (specialChars.includes(char)) {
      result += '\\' + char;
    } else {
      result += char;
    }
  }

  return result;
}

// ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° Ð½Ð° Ð½ÐµÐ·Ð°ÐºÑ€Ñ‹Ñ‚Ñ‹Ðµ Ñ‚ÐµÐ³Ð¸ (Ð½Ð°Ð¿Ñ€Ð¸Ð¼ÐµÑ€, *Ñ‚ÐµÐºÑÑ‚* â†’ ÐµÑÐ»Ð¸ Ð½ÐµÑ‚ Ð²Ñ‚Ð¾Ñ€Ð¾Ð¹ *)
function validateMarkdown(text) {
  const markers = ['*', '_', '`', '['];
  let stack = [];

  for (let i = 0; i < text.length; i++) {
    const char = text[i];
    if (markers.includes(char) && text[i - 1] !== '\\') {
      if (stack.length > 0 && stack[stack.length - 1] === char) {
        stack.pop(); // Ð—Ð°ÐºÑ€Ñ‹Ñ‚Ñ‹Ð¹ Ñ‚ÐµÐ³
      } else {
        stack.push(char); // ÐžÑ‚ÐºÑ€Ñ‹Ñ‚Ñ‹Ð¹ Ñ‚ÐµÐ³
      }
    }
  }

  if (stack.length > 0) {
    throw new Error(`Unclosed Markdown tags: ${stack.join(', ')}`);
  }

  return true;
}

// ÐŸÑ€Ð¸Ð¼ÐµÑ€ Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ð½Ð¸Ñ
