    }

    // Ð£Ð´Ð°Ð»ÑÐµÐ¼ URL Ð¸Ð· Ð¸ÑÑ…Ð¾Ð´Ð½Ð¾Ð³Ð¾ Ñ‚ÐµÐºÑÑ‚Ð°
    textWithoutUrl = (
        text.substring(0, urlStart) + 
        text.substring(urlStart + imageUrl.length)
    ).replace(/\n{2,}/g, '\n').trim(); // Ð£Ð±Ð¸Ñ€Ð°ÐµÐ¼ Ð»Ð¸ÑˆÐ½Ð¸Ðµ Ð¿ÐµÑ€ÐµÐ½Ð¾ÑÑ‹
}

// Ð”ÐµÐºÐ¾Ð´Ð¸Ñ€ÑƒÐµÐ¼ URL (ÐµÑÐ»Ð¸ Ð½ÑƒÐ¶Ð½Ð¾)
if (imageUrl) {
