// Ð“Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€ÑƒÐµÐ¼ Ñ‚ÐµÐ¼Ð¿ÐµÑ€Ð°Ñ‚ÑƒÑ€Ñƒ Ð¿Ð¾ Ð´Ð½ÑÐ¼
const dailyTemperatures = {};
const daysOfWeek = ['Ð’Ð¾ÑÐºÑ€ÐµÑÐµÐ½ÑŒÐµ', 'ÐŸÐ¾Ð½ÐµÐ´ÐµÐ»ÑŒÐ½Ð¸Ðº', 'Ð’Ñ‚Ð¾Ñ€Ð½Ð¸Ðº', 'Ð¡Ñ€ÐµÐ´Ð°', 'Ð§ÐµÑ‚Ð²ÐµÑ€Ð³', 'ÐŸÑÑ‚Ð½Ð¸Ñ†Ð°', 'Ð¡ÑƒÐ±Ð±Ð¾Ñ‚Ð°'];

weatherData.hourly.time.forEach((timestamp, index) => {
  const date = new Date(timestamp);
  const dayKey = date.toISOString().split('T')[0];

  if (!dailyTemperatures[dayKey]) {
    dailyTemperatures[dayKey] = {
      dateObj: date,
      temps: [],
      minTemp: Infinity,
      maxTemp: -Infinity
    };
  }

  const temp = weatherData.hourly.temperature_2m[index];
  dailyTemperatures[dayKey].temps.push(temp);
  dailyTemperatures[dayKey].minTemp = Math.min(dailyTemperatures[dayKey].minTemp, temp);
  dailyTemperatures[dayKey].maxTemp = Math.max(dailyTemperatures[dayKey].maxTemp, temp);
});

// Ð¤Ð¾Ñ€Ð¼Ð¸Ñ€ÑƒÐµÐ¼ Ñ‚ÐµÐºÑÑ‚ Ð´Ð»Ñ Ð²Ñ‹Ð²Ð¾Ð´Ð°
