    else if (avgTemp < 10) tempEmoji = 'â„ï¸';
    else if (avgTemp > 20) tempEmoji = 'â˜€ï¸';

    weatherText += `ðŸ“… *${dayName} ${dateStr}*\n`;
    weatherText += `${tempEmoji} Ð¢ÐµÐ¼Ð¿ÐµÑ€Ð°Ñ‚ÑƒÑ€Ð°: ${minTemp}Â°C ... ${maxTemp}Â°C\n`;
    weatherText += `ðŸ”¼ Ð¡Ñ€ÐµÐ´Ð½ÑÑ: ${roundedAvg}Â°C\n\n`;
  });

weatherText += "â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€\n";
weatherText += "â„¹ï¸ Ð”Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ñ€ÐµÐ´Ð¾ÑÑ‚Ð°Ð²Ð»ÐµÐ½Ñ‹ Open-Meteo API";

// Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‰Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð² Ð¿Ñ€Ð°Ð²Ð¸Ð»ÑŒÐ½Ð¾Ð¼ Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚Ðµ Ð´Ð»Ñ n8n
