// ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð²Ñ…Ð¾Ð´Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ
const input = $input.all()[0];
if (!input) {
  throw new Error("ÐÐµÑ‚ Ð²Ñ…Ð¾Ð´Ð½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ…!");
}

const plsqlText = input.json?.data || input.json.data || "";
if (!plsqlText) {
  throw new Error("Ð¢ÐµÐºÑÑ‚ PL/SQL Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½!");
}

// Ð ÐµÐ³ÑƒÐ»ÑÑ€Ð½Ñ‹Ðµ Ð²Ñ‹Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð¸ÑÐºÐ°
const packageRegex = /PACKAGE\s+([^\s]+)/i;
const procedureRegex = /(PROCEDURE\s+([^\s(]+)\s*\([^)]*\)\s*(?:IS|AS)[\s\S]*?END\s+\2;)/gi;
const functionRegex = /(FUNCTION\s+([^\s(]+)\s*\([^)]*\)\s*RETURN\s+[^\s]+\s*(?:IS|AS)[\s\S]*?END\s+\2;)/gi;

// Ð˜Ð·Ð²Ð»ÐµÐºÐ°ÐµÐ¼ Ð¸Ð¼Ñ Ð¿Ð°ÐºÐµÑ‚Ð°
const packageMatch = plsqlText.match(packageRegex);
const packageName = packageMatch ? packageMatch[1] : 'UNKNOWN_PKG';

const result = [];

// ÐžÐ±Ñ€Ð°Ð±Ð°Ñ‚Ñ‹Ð²Ð°ÐµÐ¼ Ð¿Ñ€Ð¾Ñ†ÐµÐ´ÑƒÑ€Ñ‹
let procedureMatch;
while ((procedureMatch = procedureRegex.exec(plsqlText)) !== null) {
  result.push({
    package: packageName,
    type: "PROCEDURE",
    name: procedureMatch[2],
    body: procedureMatch[1]
  });
}

// ÐžÐ±Ñ€Ð°Ð±Ð°Ñ‚Ñ‹Ð²Ð°ÐµÐ¼ Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¸
let functionMatch;
while ((functionMatch = functionRegex.exec(plsqlText)) !== null) {
  result.push({
    package: packageName,
    type: "FUNCTION",
    name: functionMatch[2],
    body: functionMatch[1]
  });
}

// Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‰Ð°ÐµÐ¼ Ñ€ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚
return result;

