const text = $input.first().json.output;

// ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð½Ð°Ð»Ð¸Ñ‡Ð¸Ðµ Ð½ÑƒÐ¶Ð½Ð¾Ð³Ð¾ Ð´Ð¾Ð¼ÐµÐ½Ð° Ð² Ñ‚ÐµÐºÑÑ‚Ðµ
const quickchartDomain = 'quickchart.io/chart';
let imageUrl = null;
let textWithoutUrl = null;

// Ð˜Ñ‰ÐµÐ¼ Ð½Ð°Ñ‡Ð°Ð»Ð¾ Ð»ÑŽÐ±Ð¾Ð³Ð¾ URL
const urlStart = text.search(/https?:\/\//i);
if (urlStart !== -1) {
    // Ð˜Ð·Ð²Ð»ÐµÐºÐ°ÐµÐ¼ Ñ‚ÐµÐºÑÑ‚ Ð¾Ñ‚ Ð½Ð°Ñ‡Ð°Ð»Ð° URL
    const remainingText = text.substring(urlStart);

    // Ð˜Ñ‰ÐµÐ¼ ÐºÐ¾Ð½ÐµÑ† URL (Ð¿ÐµÑ€Ð²Ñ‹Ð¹ Ð¿Ñ€Ð¾Ð±ÐµÐ» Ð¸Ð»Ð¸ Ð¿ÐµÑ€ÐµÐ²Ð¾Ð´ ÑÑ‚Ñ€Ð¾ÐºÐ¸)
    const urlEndMatch = remainingText.match(/[\n]/);
    const urlEnd = urlEndMatch ? urlEndMatch.index : remainingText.length;

    // ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð¿Ð¾Ð»Ð½Ñ‹Ð¹ URL
    const foundUrl = remainingText.substring(0, urlEnd).trim();

    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, ÑÐ¾Ð´ÐµÑ€Ð¶Ð¸Ñ‚ Ð»Ð¸ URL Ð½ÑƒÐ¶Ð½Ñ‹Ð¹ Ð´Ð¾Ð¼ÐµÐ½
    if (foundUrl.includes(quickchartDomain)) {
        imageUrl = foundUrl;

        // Ð£Ð´Ð°Ð»ÑÐµÐ¼ Ð»Ð¸ÑˆÐ½Ð¸Ðµ Ð·Ð°ÐºÑ€Ñ‹Ð²Ð°ÑŽÑ‰Ð¸Ðµ ÑÐºÐ¾Ð±ÐºÐ¸
        while (imageUrl.endsWith(')')) {
            imageUrl = imageUrl.slice(0, -1);
        }

        // Ð”ÐµÐºÐ¾Ð´Ð¸Ñ€ÑƒÐµÐ¼ URL
        try {
            imageUrl = decodeURIComponent(imageUrl);
        } catch (e) {
            // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð¾Ñ€Ð¸Ð³Ð¸Ð½Ð°Ð»ÑŒÐ½Ñ‹Ð¹ URL Ð¿Ñ€Ð¸ Ð¾ÑˆÐ¸Ð±ÐºÐµ Ð´ÐµÐºÐ¾Ð´Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
        }

        // Ð£Ð´Ð°Ð»ÑÐµÐ¼ URL Ð¸Ð· Ð¸ÑÑ…Ð¾Ð´Ð½Ð¾Ð³Ð¾ Ñ‚ÐµÐºÑÑ‚Ð°
        textWithoutUrl = (
            text.substring(0, urlStart) + 
            text.substring(urlStart + foundUrl.length)
        ).replace(/\n{2,}/g, '\n').trim();
    }
}

return [{
    json: {
        imageUrl,
        textWithoutUrl,
        originalText: text
    }
}];

