// Create folder name in dd-mm-yyyy format
const now = new Date();
const day = String(now.getDate()).padStart(2, '0');
const month = String(now.getMonth() + 1).padStart(2, '0');
const year = now.getFullYear();

const folderName = `${day}-${month}-${year}`;

return [{
  json: {
    folderName: folderName,
    timestamp: now.toISOString()
  }
}];

// Prepare workflows data for backup
const folderName = $('Create Date Folder Name').first().json.folderName;
const workflows = $input.all();

const workflowsData = {
  backupDate: new Date().toISOString(),
  folderName: folderName,
  totalWorkflows: workflows.length,
  workflows: workflows.map(item => ({
    id: item.json.id,
    name: item.json.name,
    active: item.json.active,
    tags: item.json.tags || [],
    nodes: item.json.nodes || [],
    connections: item.json.connections || {},
    settings: item.json.settings || {},
    createdAt: item.json.createdAt,
    updatedAt: item.json.updatedAt
  }))
};

return [{
  json: {
    fileName: `workflows-${folderName}.json`,
    content: JSON.stringify(workflowsData, null, 2),
    folderName: folderName
  }
}];

// Prepare credentials data for backup (without sensitive data)
const folderName = $('Create Date Folder Name').first().json.folderName;
const credentials = $input.all();

const credentialsData = {
  backupDate: new Date().toISOString(),
  folderName: folderName,
  totalCredentials: credentials.length,
  credentials: credentials.map(item => ({
    id: item.json.id,
    name: item.json.name,
    type: item.json.type,
    createdAt: item.json.createdAt,
    updatedAt: item.json.updatedAt,
    // Note: Actual credential data is not included for security
    dataIncluded: false,
    note: "Credential data excluded for security reasons"
  }))
};

return [{
  json: {
    fileName: `credentials-${folderName}.json`,
    content: JSON.stringify(credentialsData, null, 2),
    folderName: folderName
  }
}];

// Create README file for the backup
const folderName = $('Create Date Folder Name').first().json.folderName;
const workflowsResult = $('Create Workflows File in GitHub').first().json;
const credentialsResult = $('Create Credentials File in GitHub').first().json;

const readmeContent = `# n8n Backup - ${folderName}

## Backup Information
- **Date**: ${new Date().toISOString()}
- **Folder**: ${folderName}
- **Generated by**: n8n Backup Workflow

## Contents

### Files in this backup:
- \`workflows-${folderName}.json\` - All workflow definitions
- \`credentials-${folderName}.json\` - Credentials metadata (no sensitive data)
- \`README.md\` - This file

### Workflows
- Contains all workflow definitions including nodes, connections, and settings
- Can be imported back into n8n using the import functionality

### Credentials
- Contains only metadata about credentials (names, types, IDs)
- **Important**: Actual credential data is NOT included for security reasons
- You will need to manually reconfigure credentials when restoring

## Restore Instructions

1. **Workflows**: Use n8n's import feature to restore workflows from \`workflows-${folderName}.json\`
2. **Credentials**: Manually recreate credentials based on the metadata in \`credentials-${folderName}.json\`

## Security Note
This backup does not contain sensitive credential data. All API keys, passwords, and other sensitive information must be reconfigured manually during restore.

---
*Generated automatically by n8n Backup Workflow*
`;

return [{
  json: {
    fileName: `README.md`,
    content: readmeContent,
    folderName: folderName
  }
}];

